import React, { useState } from 'react';
import './App.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Navbar from 'react-bootstrap/Navbar';
import Jumbotron from 'react-bootstrap/Jumbotron';
// import {Meteo} from './widgets/meteo';
// import {Recipe} from './widgets/recette';

const axios = require('axios');

function App() {
  const [showAdmin, setAdmin] = useState(false);
  const [sexe, setSexe] = useState();

  const handleSubmit = (event) => {
    const form = event.currentTarget;
    event.preventDefault();
    event.stopPropagation();
    axios.post('/form-submit', { sexe })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  };

  function handleSexeChange(e){
    setSexe(e.target.value);
  }

  return (
    <div>
      <Navbar bg="dark" expand="lg">
        <Button className="m-2" onClick={() => setAdmin(!showAdmin)}>
          {showAdmin ? 'Back' : 'Login'}
        </Button>
        {showAdmin ? 
          <h2 color="white">    Please fill in the following form !</h2> : <p1></p1>}
      </Navbar>
      <div className="container mt-5">
      {showAdmin ?
      <div>
        <p1>Please fill in the following form !</p1>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formBasicWeight">
            <Form.Label>Your weight</Form.Label>
            <Form.Control type="number" placeholder="Your weight" />
            <Form.Text className="text-muted">
              This data is not shared with third parties.
            </Form.Text>
          </Form.Group>

          <Form.Group controlId="formBasicSporttype">
            <Form.Label>Sporttype</Form.Label>
            <Form.Control type="text" placeholder="Sporttype" />
          </Form.Group>
          <Form.Group controlId="formBasicSporttype">
            <Form.Label>How many hours you practiced this sporttype?</Form.Label>
            <Form.Control type="text" placeholder="Sporttype" />
          </Form.Group>
          <Form.Group controlId="formBasicSporttype">
            <Form.Label>How many days you practiced per week?</Form.Label>
            <Form.Control type="text" placeholder="Days per week" />
          </Form.Group>
          <Form.Group controlId="formBasicSexe">
            <Form.Label>Gender</Form.Label>
            {['Male', 'Female', 'Divers'].map(label => (
              <Form.Check
                key={label}
                id={label}
                type="radio"
                name="sexe"
                value={label}
                label={label}
                checked={sexe === label}
                onChange={handleSexeChange}
              />
            ))}
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
        </div>
        :
        <Jumbotron>
          <h1>Welcome</h1>
          <p>
            To use this site, please log in by using the button in the top left corner.
          </p>
        </Jumbotron>
    }
    </div>
    </div>
  );
}

export default App;
