import React from 'react';
import { action } from '@storybook/addon-actions';
import { Button } from '@storybook/react/demo';
import { storiesOf } from '@storybook/react';
import { withKnobs, text, boolean } from '@storybook/addon-actions/addon-knobs/react';


export default {
  title: 'Button',
};

export const texte = () => <Button onClick={action('clicked')}>Hello Button</Button>;

export const emoji = () => (
  <Button onClick={action('clicked')}>
    <span role="img" aria-label="so cool">
      😀 😎 👍 💯
    </span>
  </Button>
);

export const primary = () => (
  <Button id="input" background="#65C7F0" onClick={action('clicked')}>change</Button>);
  

const stories = storiesOf("Storybook Knobs", module);
stories.addDecorator(withKnobs); 

stories.add("avec button", () => (
  <button disabled={false}>{text("Label", "hi")}</button>
));
