import React, { useState } from 'react';
import './App.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Navbar from 'react-bootstrap/Navbar';
import Jumbotron from 'react-bootstrap/Jumbotron';
// import {Meteo} from './widgets/meteo';
// import {Recipe} from './widgets/recette';

const axios = require('axios');

function App() {
  const [showAdmin, setAdmin] = useState(false);
  const [sexe, setSexe] = useState();

  const handleSubmit = (event) => {
    const form = event.currentTarget;
    event.preventDefault();
    event.stopPropagation();
    axios.post('/form-submit', { sexe })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  };

  function handleSexeChange(e){
    setSexe(e.target.value);
  }

  return (
    <div>
      <Navbar bg="dark" expand="lg">
        <Button className="m-2" onClick={() => setAdmin(!showAdmin)}>
          {showAdmin ? 'Back' : 'Login'}
        </Button>
      </Navbar>
      <div className="container mt-5">
      {showAdmin ?
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email" />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" />
          </Form.Group>
          <Form.Group controlId="formBasicCheckbox">
            <Form.Label>Sexe</Form.Label>
            {['Male', 'Female', 'Divers'].map(label => (
              <Form.Check
                key={label}
                id={label}
                type="radio"
                name="sexe"
                value={label}
                label={label}
                checked={sexe === label}
                onChange={handleSexeChange}
              />
            ))}
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
        :
        <Jumbotron>
          <h1>Welcome</h1>
          <p>
            To use this site, please log in by using the button in the top left corner.
          </p>
        </Jumbotron>
    }
    </div>
    </div>
  );
}

export default App;
