import './recette.css';
import React from 'react';
import axios from "axios";
import CanvasJSReact from '../canvasjs.react';
//var CanvasJSReact = require('./canvasjs.react');
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;


const API_KEY = "afcaada881f54b3e87dac57c23cff390";
const API_URL = "https://api.spoonacular.com/recipes/complexSearch";
//const API_URL = "https://api.spoonacular.com/recipes/716429/information";
// const API_IMAGE_URL = "https://spoonacular.com/recipeImages/";


export class Recipe extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeRecipe: 0,
      recipes: [{
        title: 'TestRezept',
        image: '',
        calories: 800,
        protein: 60,
        fat: 300,
        sugar: 80,
      },{
        title: 'TestRezept2',
        image: '',
        calories: 700,
        protein: 80,
        fat: 360,
        sugar: 180,
      }]
     }
    this.fetchRecipe = this.fetchRecipe.bind(this);
  }

  writeRecipesToState(result){
    const newRecipe = {
      title: result.title,
      image: result.image,
      calories: result.nutrition.nutrients[0].amount,
      protein: result.nutrition.nutrients[1].amount,
      fat: result.nutrition.nutrients[2].amount,
      sugar: result.nutrition.nutrients[3].amount,
    }
    this.setState({
      recipes: [
        ...this.state.recipes,
        newRecipe
      ]
    })
  }

  componentDidMount(){
    this.fetchRecipe()
    .then((response) => {
      // Récupère la donnée d'une API
      const data = response.data;
      console.log(data);

      for(const result in data.results) {
        this.writeRecipesToState(result);
      }
      console.log(this.state);
  })
  .catch(console.error);
}
  fetchRecipe(){
    return axios
    //assemble API with Key and set nutrition parameters
   // .get(`${API_URL}?apiKey=${API_KEY}&includeNutrition=true`, {
      .get(`${API_URL}?apiKey=${API_KEY}&maxFat=25&maxSugar=30&minProtein=25&maxCalories=600&number=3`, {
      crossdomain: true
    })
  }

  getHTMLElementFromImage(image){
    //return `<img src=${API_IMAGE_URL}${recipeID}-312x231@2x.jpg class="weather-icon"/>`
  }

  Slidebutton(props){
  return(
    <button className="slidebutton" onClick={props.onclick}></button>
  )
}

  render(){
    const recipe = this.state.recipes[this.state.activeRecipe];
    const options = {
      title: {
        text: "Basic Column Chart in React"
      },
      data: [{				
        type: "pie",
        startAngle: 240,
        yValueFormatString: "##0.0\"g\"",
        indexLabel: "{label} {y}",
        dataPoints: [
          {y: recipe.protein, label: "Protéines"},
          {y: recipe.fat, label: "Gras"},
          {y: recipe.sugar, label: "Sucre"},
        ]
       }]
  }
  console.log(options)
    return(
      <div>
        {this.state.recipes.length > 0 &&
         <CanvasJSChart options = {options}></CanvasJSChart>
        }

      <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
      <ol className="carousel-indicators">
      {this.state.recipes.map((recipe, index) => (
          <li
            data-target="#carouselExampleIndicators"
            data-slide-to={index}
            className={`${index === this.state.activeRecipe ? 'active' : ''}`}
            key={recipe.title}>
          </li>
        ))}
      </ol>
      <div className="carousel-inner">
        {this.state.recipes.map((recipe, index) => (
          <div className={`carousel-item ${index === this.state.activeRecipe ? 'active' : ''}`} key={recipe.title}>
            <span className="titel">{recipe.title}</span>
            <span className="conditions"><img src={`${recipe.image}`} alt={recipe.title}></img></span>
          </div>
        ))}
      </div>
      <a className="carousel-control-prev" href="#carouselExampleIndicators" role="button" data-slide="prev">
        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
        <span className="sr-only">Previous</span>
      </a>
      <a className="carousel-control-next" href="#carouselExampleIndicators" role="button" data-slide="next">
        <span className="carousel-control-next-icon" aria-hidden="true"></span>
        <span className="sr-only">Next</span>
      </a>
    </div>
    </div>

    )
  }
}
