import './fonction.css'
import React from "react";
import Popup from "reactjs-popup";

function popup(){

  
  return(

    window.open(URL="connexion.html")
    
    /*
  <Popup trigger={<button> Trigger</button>} position="right center">
    <div>Popup content here !!</div>
  </Popup>*/


  );
}
export default popup;



